from sage.all import *

# f is the monic polynomial f(x) = (m + x)**e - c whose roots we have to find
def cop(partial, n, c, e):
    P = PolynomialRing(Zmod(n), names=('x',)); (x,) = P._first_ngens(_sage_const_1 )
    f = (partial + x)**e - c
    f = f.monic()
    m = f.small_roots(epsilon=_sage_const_1 /_sage_const_20 )
    return m[_sage_const_0 ]


